import React, { useState } from 'react';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Bell, Search, ChevronDown, LayoutDashboard, Settings, Sun, Zap, BatteryCharging, ArrowLeftRight, History, PanelTop, PlusCircle, Menu } from 'lucide-react';
import './App.css';

// --- INITIAL MOCK DATA --- //
const initialPowerData = [
  { time: '00:00', generation: 0, consumption: 300, battery: 85 },
  { time: '03:00', generation: 0, consumption: 250, battery: 75 },
  { time: '06:00', generation: 150, consumption: 400, battery: 65 },
  { time: '09:00', generation: 2500, consumption: 700, battery: 80 },
  { time: '12:00', generation: 4500, consumption: 1200, battery: 95 },
  { time: '15:00', generation: 3800, consumption: 1100, battery: 98 },
  { time: '18:00', generation: 800, consumption: 1500, battery: 70 },
  { time: '21:00', generation: 0, consumption: 900, battery: 55 },
];

const initialEventLog = [
  { id: 1, time: '15:02', event: 'Battery Full', details: 'Stopped charging, switched to grid export.', type: 'success' },
  { id: 2, time: '12:35', event: 'Peak Generation', details: 'Solar generation reached 4.5 kW.', type: 'success' },
  { id: 3, time: '09:15', event: 'Charging Started', details: 'Battery charging initiated from solar.', type: 'info' },
  { id: 4, time: '07:00', event: 'Grid Import', details: 'Started importing power from grid.', type: 'warning' },
  { id: 5, time: '06:10', event: 'Sunrise', details: 'Solar panels started generating power.', type: 'success' },
];

// --- DASHBOARD COMPONENTS --- //

const StatCard = ({ icon, title, value, change, iconBgClass }) => {
  const IconComponent = icon;
  return (
    <div className="stat-card">
      <div className={`stat-card-icon-wrapper ${iconBgClass}`}>
        <IconComponent />
      </div>
      <div className="stat-card-info">
        <p className="stat-card-title">{title}</p>
        <p className="stat-card-value">{value}</p>
        {change && <p className="stat-card-change">{change}</p>}
      </div>
    </div>
  );
};

const Sidebar = ({ isOpen }) => {
  const navItems = [
    { icon: LayoutDashboard, name: 'Dashboard' },
    { icon: History, name: 'History' },
    { icon: Settings, name: 'Settings' },
  ];

  return (
    <aside className={`sidebar ${isOpen ? 'open' : ''}`}>
      <div className="sidebar-header">
        <PanelTop style={{color: 'var(--tangerine-primary)', width: '2rem', height: '2rem'}} />
        <h1 className="sidebar-title">Solar Dash</h1>
      </div>
      <nav className="sidebar-nav">
        {navItems.map((item, index) => (
          <a key={index} href="#" className={`nav-item ${index === 0 ? 'active' : ''}`}>
            <item.icon className="nav-item-icon" />
            <span className="nav-item-text">{item.name}</span>
          </a>
        ))}
      </nav>
      <div className="sidebar-footer">
           <div className="support-box">
              <p>Need help?</p>
              <a href="#">Contact Support</a>
           </div>
      </div>
    </aside>
  );
};

const Header = ({ onMenuClick }) => {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  return (
    <header className="header">
      <div className="header-content">
        <button onClick={onMenuClick} className="mobile-menu-button">
          <Menu />
        </button>

        <div className="search-bar-container">
          <Search className="search-icon" size={20} />
          <input
            type="text"
            placeholder="Search events or devices..."
            className="search-input"
          />
        </div>

        <div className="header-right">
          <button className="notification-button">
            <Bell size={24} />
            <span className="notification-dot-container">
              <span className="notification-dot-ping"></span>
              <span className="notification-dot"></span>
            </span>
          </button>

          <div style={{position: 'relative'}}>
            <button onClick={() => setDropdownOpen(!dropdownOpen)} className="profile-button">
              <img
                className="profile-avatar"
                src="https://placehold.co/100x100/F97316/ffffff?text=U"
                alt="User avatar"
              />
              <span className="profile-name">System User</span>
              <ChevronDown className={`profile-chevron ${dropdownOpen ? 'open' : ''}`} size={20} />
            </button>
            {dropdownOpen && (
              <div className="profile-dropdown">
                <a href="#">Profile</a>
                <a href="#">Settings</a>
                <a href="#">Logout</a>
              </div>
            )}
          </div>
        </div>
      </div>
    </header>
  );
};

const DataEntryForm = ({ onAddData }) => {
  const [time, setTime] = useState('');
  const [generation, setGeneration] = useState('');
  const [consumption, setConsumption] = useState('');
  const [battery, setBattery] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!time || !generation || !consumption || !battery) return;
    const newDataPoint = {
      time,
      generation: parseInt(generation, 10),
      consumption: parseInt(consumption, 10),
      battery: parseInt(battery, 10)
    };
    onAddData(newDataPoint);
    setTime(''); setGeneration(''); setConsumption(''); setBattery('');
  };

  return (
    <div className="form-container">
      <h3 className="section-title">Add New Data Point</h3>
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <label>Time (HH:MM)</label>
          <input type="time" value={time} onChange={(e) => setTime(e.target.value)} className="form-input" required />
        </div>
        <div className="form-row">
          <label>Generation (W)</label>
          <input type="number" value={generation} onChange={(e) => setGeneration(e.target.value)} placeholder="e.g., 3500" className="form-input" required />
        </div>
        <div className="form-row">
          <label>Consumption (W)</label>
          <input type="number" value={consumption} onChange={(e) => setConsumption(e.target.value)} placeholder="e.g., 800" className="form-input" required />
        </div>
        <div className="form-row">
          <label>Battery (%)</label>
          <input type="number" value={battery} onChange={(e) => setBattery(e.target.value)} placeholder="e.g., 90" className="form-input" required />
        </div>
        <button type="submit" className="submit-button">
          <PlusCircle size={20} />
          Add Data Point
        </button>
      </form>
    </div>
  );
};

const DashboardContent = ({ powerData, eventLog, onAddData }) => {
  const getEventTypeChipClass = (type) => {
    switch (type) {
      case 'success': return 'success';
      case 'warning': return 'warning';
      case 'info': return 'info';
      default: return '';
    }
  };

  return (
    <main className="main-content">
      <h2 className="page-title">Real-time Solar Overview</h2>

      <div className="stat-cards-grid">
        <StatCard icon={Sun} title="Live Generation" value="4.2 kW" iconBgClass="sun" change="Peak today: 4.5 kW"/>
        <StatCard icon={Zap} title="Live Consumption" value="1.1 kW" iconBgClass="zap" change="Home usage"/>
        <StatCard icon={BatteryCharging} title="Battery Status" value="98%" iconBgClass="battery" change="Charging (Solar)"/>
        <StatCard icon={ArrowLeftRight} title="Grid Status" value="3.1 kW" iconBgClass="grid" change="Exporting to Grid"/>
      </div>

      <div className="chart-container">
        <h3 className="chart-title">Today's Power Metrics</h3>
        <div className="chart-wrapper">
          <ResponsiveContainer width="100%" height="100%">
            <AreaChart data={powerData} margin={{ top: 5, right: 20, left: -10, bottom: 5 }}>
              <defs>
                  <linearGradient id="colorGeneration" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="5%" stopColor="#F97316" stopOpacity={0.8}/>
                      <stop offset="95%" stopColor="#F97316" stopOpacity={0}/>
                  </linearGradient>
                  <linearGradient id="colorConsumption" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="5%" stopColor="#64748B" stopOpacity={0.8}/>
                      <stop offset="95%" stopColor="#64748B" stopOpacity={0}/>
                  </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" stroke="var(--border-color)" />
              <XAxis dataKey="time" stroke="var(--text-secondary)" />
              <YAxis stroke="var(--text-secondary)" unit="W" />
              <Tooltip contentStyle={{ borderRadius: '0.5rem', boxShadow: 'var(--shadow-md)', border: '1px solid var(--border-color)' }} />
              <Legend wrapperStyle={{ color: 'var(--text-secondary)' }}/>
              <Area type="monotone" dataKey="generation" stroke="#F97316" fillOpacity={1} fill="url(#colorGeneration)" strokeWidth={2} />
              <Area type="monotone" dataKey="consumption" stroke="#64748B" fillOpacity={1} fill="url(#colorConsumption)" strokeWidth={2} />
            </AreaChart>
          </ResponsiveContainer>
        </div>
      </div>

      <div className="bottom-grid">
          <div className="events-container">
              <h3 className="section-title">Recent Events</h3>
              <div className="table-wrapper">
                  <table className="events-table">
                      <thead>
                          <tr><th>Time</th><th>Event</th><th>Details</th></tr>
                      </thead>
                      <tbody>
                          {eventLog.map(log => (
                              <tr key={log.id}>
                                  <td>{log.time}</td>
                                  <td><span className={`event-chip ${getEventTypeChipClass(log.type)}`}>{log.event}</span></td>
                                  <td>{log.details}</td>
                              </tr>
                          ))}
                      </tbody>
                  </table>
              </div>
          </div>
          <DataEntryForm onAddData={onAddData} />
      </div>

    </main>
  );
};

// --- MAIN APP COMPONENT --- //
export default function App() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [powerData, setPowerData] = useState(initialPowerData);
  const [eventLog, setEventLog] = useState(initialEventLog);

  const handleAddData = (newDataPoint) => {
    setPowerData(prevData => [...prevData, newDataPoint].sort((a, b) => a.time.localeCompare(b.time)));
    const newEvent = {
      id: eventLog.length + 1,
      time: new Date().toLocaleTimeString('en-GB', { hour: '2-digit', minute: '2-digit' }),
      event: 'Manual Entry',
      details: `Gen: ${newDataPoint.generation}W, Cons: ${newDataPoint.consumption}W`,
      type: 'info'
    };
    setEventLog(prevLog => [newEvent, ...prevLog]);
  };

  return (
    <div className="app-container">
      {sidebarOpen && <div className="mobile-overlay" onClick={() => setSidebarOpen(false)}></div>}
      <Sidebar isOpen={sidebarOpen} />
      <div className="content-wrapper">
        <Header onMenuClick={() => setSidebarOpen(!sidebarOpen)} />
        <DashboardContent powerData={powerData} eventLog={eventLog} onAddData={handleAddData} />
      </div>
    </div>
  );
}
